CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `v_existencias_copy` AS SELECT
product.id as idprod, 
product.barcode,
model.`code`,
model.description,
category.`name` as category,
brand.`name` as brand,
IFNULL((SELECT SUM(billdetail.cant) FROM billdetail INNER JOIN bill on bill.id= billdetail.bill WHERE bill.type=1 AND billdetail.product= idprod),0) as counting,
IFNULL((SELECT SUM(billdetail.cant) FROM billdetail INNER JOIN bill on bill.id= billdetail.bill WHERE bill.type=2 AND billdetail.product= idprod),0) as countegs,
(SELECT counting-countegs) as enbodega,
IF((MOD( (SELECT counting-countegs),2)),'EN BODEGA', 'ENTREGADO') as estado
FROM
product
INNER JOIN model ON product.model = model.id
INNER JOIN category ON model.category = category.id
INNER JOIN brand ON model.brand = brand.id ;

